import { useState, useEffect } from "react";
import LineChart from "./LineChart";
import axios from 'axios';
import Combobox from "react-widgets/Combobox";


function BuyingCard() {
    const[neighborhood, setNeighborhood]=useState([
        "Barajevo", 
        "Cukarica", 
        "Grocka", 
        "Lazarevac",
        "Mladenovac",
        "Novi Beograd",
        "Obrenovac",
        "Palilula",
        "Rakovica",
        "Savski venac",
        "Sopot",
        "Stari grad",
        "Surcin",
        "Vozdovac",
        "Vracar",
        "Zemun",
        "Zvezdara"
    ]);
    const roomCount=['0.5', '1.0', '1.5', '2.0', '2.5', '3.0', '3.5', '4.0', '4.5', '5.0'];
    const[historicalPrices, setHistoricalPrices]=useState([]);
    const [forecast, setForecast] = useState([]);
    const [lowerBoundForecast, setLowerBoundForecast] = useState([]);
    const [upperBoundForecast, setUpperBoundForecast] = useState([]);
    const [flag,setFlag] = useState(false);

    const [realEstateData, setRealEstateData] = useState({
        location:'',
        roomCount:'',
    });
    
    function forecastPrice (e){
        // console.log(realEstateData)
        e.preventDefault();
        axios.post('https://localhost:44338/api/PriceAnalyser/forecast', realEstateData)
            .then((res)=>{
                setHistoricalPrices(res.data.historicalData);
                setForecast(res.data.forecast);
                setUpperBoundForecast(res.data.upperBoundForecast);
                setLowerBoundForecast(res.data.lowerBoundForecast);
            })
            .then(()=>{
              setChartData(forecast);
            })
    }

    // useEffect(() => {
    //   // setChartData({...historicalPrices, ...forecast});
    //   setChartData(forecast);
    // }, [flag]);


    const [chartData, setChartData] = useState({});

    var data = {
    labels: Object.entries(chartData).map(([key,value]) => key),
    datasets: [
      {
        label: "Prosečna cena nekretnina",
        data: Object.entries(chartData).map((value) => value),
        backgroundColor: [
          "rgba(75,192,192,1)",
          // "#ecf0f1",
          // "#50AF95",
          // "#f3ba2f",
          // "#2a71d0",
        ],
        borderColor: "black",
        borderWidth: 2,
        
      },
    ]
  }

  var options = {
    maintainAspectRatio: false,
    scales: {
    },
    legend: {
      labels: {
        fontSize: 25,
      },
    },
  }


  return (
    <div>
        <form>
        <div>
                    <label>Naselje</label>
                    <Combobox
                        defaultValue=""
                        data={neighborhood}
                        onChange={(value)=> realEstateData.location=value}
                        placeholder="Izaberite naselje"
                        style={{marginBottom:20}}
                        aria-required = 'true'
                    />
                </div>
                <div>
                    <label>Broj soba</label>
                    <Combobox
                        defaultValue=""
                        data={roomCount}
                        placeholder="Izaberite broj soba"
                        onChange={value=>realEstateData.roomCount=value}
                        style={{marginBottom:20}}
                        aria-required = 'true'
                    />
                </div>
                <button onClick={forecastPrice} className="btnCalculate">
                        Predvidi cenu
                    </button>
        </form>
    <div className="App">
      <div style={{ width: 700 }}>
        <LineChart chartData={data} options={options} height={400} width={700}/>
      </div>
    </div>
    </div>
  );
}

export default BuyingCard;
