import React, { useState,useEffect } from "react"
import "react-widgets/styles.css"; 
import Combobox from "react-widgets/Combobox";
import axios from 'axios';


function SellingInputForm(props){

    const location = props.location
    const[neighborhood, setNeighborhood]=useState([]);
    const roomCount=[1, 1.5, 2, 2.5, 3, 3.5, 4, 4.5, 5];
    const heatingType=["central", "district", "electricity", "gas"] //treba da se napravi mapa na backendu
    const [price, setPrice] = useState(0);

    const[selectedNeighborhood, setSelectedNeighbordhood]=useState();
    const[selectedRoomCount, setSelectedRoomCount]=useState();
    const[selectedHeatingType, setSelectedHeatingType]=useState();


    const [realEstateData, setRealEstateData] = useState({
        location:'',
        quadrature:'',
        roomCount:'',
        heatingType:'',
        elevator:''
    });


    function handleInput(key,value){
        let inputData = realEstateData;
        inputData[key]=value;
        setRealEstateData(inputData);
    }

    const fetchData = async () => {
        let res = await axios.get('https://localhost:7149/api/Mapper/'+location);
        setNeighborhood(res.data);
        console.log(res.data)
    }

    function calculatePrice (e){
		e.preventDefault();
        axios.post('https://localhost:7149/api/Price', realEstateData)
            .then((res)=>{
                console.log(res);
                setPrice(res);
            });
    }
    
    useEffect(() => {
        if(props.location)
        fetchData();
    },[props.location]);


   

    return(
        <div>
            <form>
                <h2>Unesite podatke o nekretnini</h2>
                <div>
                    <label>Naselje</label>
                    <Combobox
                        defaultValue=""
                        data={neighborhood}
                        value={neighborhood[0]}
                        onChange={value=> realEstateData[neighborhood]=value}
                        placeholder="Izaberite naselje"
                    />
                </div>
                <div>
                    <label>Kvadratura</label>
                    <input
                        type="text"
                        placeholder="m2"
                        name="quadrature"
                        onInput={handleInput(e.target.name, e.target.value)}
                    ></input>
                </div>
                <div>
                    <label>Broj soba</label>
                    <Combobox
                        defaultValue=""
                        data={roomCount}
                        value={roomCount[0]}
                        onChange={value=>setSelectedRoomCount(value)}
                    />
                </div>
                <div>
                    <label>Grejanje</label>
                    <Combobox
                        defaultValue=""
                        data={heatingType}
                        value={heatingType[0]}
                        onChange={value=>setSelectedHeatingType(value)}
                    />
                </div>
                <div>
                    <label>Broj liftova u zgradi</label>
                    <input
                        type="text"
                        name="elevator"
                        onInput={handleInput}
                    ></input>
                </div>
                <div>
                    <button onClick={calculatePrice}>
                        Izraƒçunaj cenu
                    </button>
                </div>
                <div>
                    <h3>{price!==0 ? price : null}</h3>
                </div>
            </form>
        </div>
    )

}

export default SellingInputForm;
