import React, { useState,useEffect } from "react"
import "react-widgets/styles.css"; 
import Combobox from "react-widgets/Combobox";
import MapCard from "./MapCard";


function SellingInputForm(props){

    const location = props.location
    const MyContext = React.createContext();
    const[neighborhood, setNeighborhood]=useState([]);
    const roomCount=[1, 1.5, 2, 2.5, 3, 3.5, 4, 4.5, 5];
    const heatingType=["Etazno", "Centralno", "Struja", "Gas"] //treba da se napravi mapa na backendu
    
    const[selectedNeighborhood, setSelectedNeighbordhood]=useState();
    const[selectedRoomCount, setSelectedRoomCount]=useState();
    const[selectedHeatingType, setSelectedHeatingType]=useState();


    const [realEstateData, setRealEstateData] = useState({
        location:'',
        quadrature:'',
        roomCount:'',
        heatingType:'',
        elevator:''
    });

    function handleInput(e){
        let inputData = realEstateData;
        inputData[e.target.name]=e.target.value;
        setRealEstateData(inputData);
    }

    
    useEffect(() => {
        const fetchData = async () => {
            let res = await axios.get('https://localhost:7149/api/Mapper/'+location);
            setNeighborhood(res.data);
            console.log(res.data)
        }
    });


   

    return(
        <div>
            <form>
                <h2>Unesite podatke o nekretnini</h2>
                <div>
                    <label>Naselje</label>
                    <Combobox
                        defaultValue=""
                        data={neighborhood}
                        value={selectedNeighborhood}
                        onChange={value=>setSelectedNeighbordhood(value)}
                    />
                </div>
                <div>
                    <label>Kvadratura</label>
                    <input
                        type="text"
                        placeholder="m2"
                        name="quadrature"
                        onInput={handleInput}
                    ></input>
                </div>
                <div>
                    <label>Broj soba</label>
                    <Combobox
                        defaultValue=""
                        data={roomCount}
                        value={selectedRoomCount}
                        onChange={value=>setSelectedRoomCount(value)}
                    />
                </div>
                <div>
                    <label>Grejanje</label>
                    <Combobox
                        defaultValue=""
                        data={heatingType}
                        value={selectedHeatingType}
                        onChange={value=>setSelectedHeatingType(value)}
                    />
                </div>
                <div>
                    <label>Broj liftova u zgradi</label>
                    <input
                        type="text"
                        name="elevator"
                        onInput={handleInput}
                    ></input>
                </div>
            </form>
        </div>
    )

}

export default SellingInputForm;
